require 'test/unit'

# TC_String contains unit tests for the String class.
#
# API Object::       String
# C++ File::         rstring.cpp
# Parent Class::     Object
# Version::          SketchUp 6.0
#
# The String class contains a method used to parse a string as a length value.
#
class TC_String < Test::Unit::TestCase

  # Setup for test cases, if required.
  # 
  def setup

  end

  # ----------------------------------------------------------------------------
  # @par Ruby Method:    String.to_l
  # @file                runits.cpp
  #
  # The to_l method is used to convert a string to a length.
  #
  #
  # Args:
  #
  # Returns:
  # - length: the length value
  #

  # Test the example code that we have in the API documentation.
  def test_to_l_api_example
    length = nil
    assert_nothing_raised do
     length = "5' 6\"".to_l
    end
    expected_length = 66
    assert_equal(expected_length, length,
                 'Failed in test_to_l' )
  end

  # Test edgecases for values passed to this method.
  def test_to_l_edgecases
    length = nil
    assert_nothing_raised do
      length = "0".to_l
    end
    expected_length = 0
    assert_equal(expected_length, length,
                 'Failed in test_to_l_edgecases' )  
  end

  # Test what happens when bad arguments are passed (nil, too few, etc.)
  def test_to_l_bad_params
    assert_raise ArgumentError do
      length = "Sandra".to_l
      # bad arguments here that should cause errors
    end
  end

end
